import React from 'react';
import './PropertyAttributesComponent.css'; // You can define your styles in this CSS file

const BedComponent = ({ count }) => (
  <div className="bed-component">
    {Array.from({ length: count }, (_, index) => (
      <span key={index} role="img" aria-label="bed">
        üõèÔ∏è
      </span>
    ))}
  </div>
);

const PropertyAttributesComponent = ({ property }) => {
  // Check if property or property.image is undefined
  if (!property || !property.image) {
    return null; // Render nothing if the data is missing
  }

  return (
    <div className="property-attributes">
      <div className="property-attributes-image">
        <img src={property.image} alt={property.name} />
      </div>
      <div className="property-attributes-details">
        <h3>{property.name}</h3>
        <p>{property.address}</p>
        <BedComponent count={property.numBeds || 0} />
        <div className="bathroom-component">
          <img
            src="/path/to/tick-mark-image.png"
            alt="Tick Mark"
            style={{ width: '20px', height: '20px' }}
          />
          <p>{property.numBathrooms || 0} Bathrooms</p>
        </div>
        <p>{property.squareFeet || 0} sq. ft.</p>
      </div>
    </div>
  );
};

export default PropertyAttributesComponent;
