import React from 'react';

const BedComponent = ({ count }) => (
  <div className="bed-component">
    {Array.from({ length: count }, (_, index) => (
      <span key={index} role="img" aria-label="bed">
        üõèÔ∏è
      </span>
    ))}
  </div>
);

const PropertyComponent = ({ propertyData }) => (
  <div className="flex flex-wrap justify-around">
    {propertyData.map((property, index) => (
      <div key={index} className="max-w-md bg-gray-200 p-6 rounded-md m-4">
        <div className="property-image">
          <img
            src={property.imageUrl}
            alt={`Property ${index + 1}`}
            className="w-full h-32 object-cover rounded-md mb-4"
          />
        </div>
        <div className="property-details">
          <p className="text-xl font-semibold mb-2">{property.name}</p>
          <p className="text-gray-700 mb-2">{property.address}</p>
          <BedComponent count={property.numBeds} />
          <div className="flex items-center mt-2">
            <img
              src="/path/to/tick-mark-image.png"
              alt="Tick Mark"
              className="w-4 h-4 mr-2"
            />
            <p>{property.numBathrooms} Bathrooms</p>
          </div>
          <p className="text-gray-700 mt-2">{property.squareFeet} sq. ft.</p>
        </div>
      </div>
    ))}
  </div>
);

export default PropertyComponent;
