import React from 'react';
import './PropertyComponent.css'; // You can define your styles in this CSS file

const BedComponent = ({ count }) => (
  <div className="bed-component">
    {Array.from({ length: count }, (_, index) => (
      <span key={index} role="img" aria-label="bed">
        üõèÔ∏è
      </span>
    ))}
  </div>
);

const PropertyComponent = ({ propertyData }) => (
  <section id="properties">
    <h5>Featured Properties</h5>
    <h2>Properties</h2>
    <div className="property-container">
      {propertyData.map(({ id, image, name, address, numBeds, numBathrooms, squareFeet }) => (
        <div key={id} className="property-item">
          <div className="property-image" style={{ background: 'grey' }}>
            <img src={image} alt={name} />
          </div>
          <div className="property-details">
            <p className="property-name">{name}</p>
            <p className="property-address">{address}</p>
            <BedComponent count={numBeds} />
            <div className="bathroom-component">
              {/* Assuming you have a tick mark image for bathrooms */}
              <img
                src="/path/to/tick-mark-image.png"
                alt="Tick Mark"
                style={{ width: '20px', height: '20px' }}
              />
              <p>{numBathrooms} Bathrooms</p>
            </div>
            <p className="property-square-feet">{squareFeet} sq. ft.</p>
          </div>
        </div>
      ))}
    </div>
  </section>
);

export default PropertyComponent;
