import React from 'react';

const PropertyCard = ({ cost, propertyName, address, imageUrl }) => {
  return (
    <div className="max-w-sm mx-auto bg-gray-200 rounded overflow-hidden shadow-lg mb-8 mt-1">
      <div>
        <img className="w-full h-48 object-cover mb-2" src={imageUrl} alt="Property" />
      </div>
      <div className="px-6 py-4">
        <p className="text-gray-700 text-base">
          <span className="text-blue-500 text-xl text-lg font-bold">&#8377;{cost}</span> <strong>/month</strong>
        </p>
        <div className="font-bold text-xl mb-2">{propertyName}</div>
        <p className="text-gray-900 text-base">
          <strong>Address:</strong> {address}
        </p>
      </div>
    </div>
  );
};

const PropertyList = () => {
  const properties = [
    // ... (your property data)
  ];

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="grid grid-cols-3 gap-4">
        {properties.map((property) => (
          <PropertyCard key={property.id} {...property} />
        ))}
      </div>
    </div>
  );
};

export default PropertyList;
